    AND P.CALCULATE_STATUS = #{calculateStatus,jdbcType=VARCHAR}


    AND P.ID > #{lowRowNum,jdbcType=BIGINT}
    AND P.ID <= #{highRowNum,jdbcType=BIGINT}

SELECT * FROM (
SELECT
ROW_NUMBER() OVER(ORDER BY P.ID ASC) AS ROWNUM,
ID
FROM YMF_PROFIT P
WHERE P.PROFIT_PRODUCT_TYPE = #{profitType,jdbcType=VARCHAR}
AND P.CUSTOMER_TYPE = #{customerType,jdbcType=VARCHAR}
AND P.MONTH = #{month,jdbcType=VARCHAR}

    AND P.CALCULATE_STATUS = #{calculateStatus,jdbcType=VARCHAR}

) A WHERE
ROWNUM IN

    #{item}

SELECT P.PROFIT_PRODUCT_TYPE, P.CUSTOMER_TYPE, P.MONTH, COUNT(*) TOTAL_COUNT, SUM(P.TOTAL_TRX_COUNT) TOTAL_TRX_COUNT, SUM(P.DAY_COUNT) TOTAL_DAY_COUNT, SUM(p.TRX_AMT) TOTAL_TRX_AMT, SUM(p.PROFIT_AMT) TOTAL_PROFIT_AMT, SUM(p.MITANG_TRXAMT) MITANG_TOTAL_TRXAMT, SUM(p.MITANG_PROFIT_AMT) MITANG_TOTAL_PROFIT_AMT FROM YMF_PROFIT P WHERE P.PROFIT_PRODUCT_TYPE = #{profitType,jdbcType=VARCHAR} AND P.CUSTOMER_TYPE = #{customerType,jdbcType=VARCHAR} AND P.MONTH = #{month,jdbcType=VARCHAR}
AND P.CALCULATE_STATUS = #{calculateStatus,jdbcType=VARCHAR}
GROUP BY P.PROFIT_PRODUCT_TYPE,P.CUSTOMER_TYPE,P.MONTH

SELECT
COUNT(*) TOTAL_COUNT
FROM YMF_PROFIT P
WHERE P.PROFIT_PRODUCT_TYPE = #{profitType,jdbcType=VARCHAR}
